import React, { useEffect, useState } from 'react';
import './Profile.scss';
import {
    IconButton,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TablePagination,
    TableRow
} from '@material-ui/core';
import { db } from '../../firebase';
import { useScreenSize } from '../../hooks/useScreenSize';
import { toDate } from '../../utils/toDate';
import { Archive, Unarchive } from '@material-ui/icons';
import SellIcon from '@mui/icons-material/Sell';
import { useParams } from 'react-router-dom';
import { fetchAds } from '../../utils/fetchUsersAds';
import { Ad } from '../../interfaces/Ad';
import { User } from '../../interfaces/User';
import { fetchUser } from '../../utils/fetchUser';
import { closeAd } from '../../utils/closeAd';

interface ProfileProps {
    myProfile?: boolean,
}

interface Column {
    id: string,
    label: string,
    minWidth: number,
    align: 'center' | 'left' | 'right' | 'justify' | 'inherit' | undefined,
}

const Profile: React.FC<ProfileProps> = (props): JSX.Element => {
    const [adsData, setAdsData] = useState<Ad[]>([]);
    const [user, setUser] = useState<User>({
        activeAds: 0,
        avatar: '',
        date: {
            seconds: 0,
            nanoseconds: 0,
        },
        email: '',
        id: '',
        name: '',
        phone: '',
    });
    const { id } = useParams<{id: string}>();
    const [rerender, setRerender] = useState(0);
    const [page, setPage] = React.useState<number>(0);

    useEffect(() => {
        const userPromise = props.myProfile ? fetchUser(1) : fetchUser(Number(id));
        userPromise.then((response1) => {
            setUser(response1);
            fetchAds(response1.id).then((response2) => {
                setAdsData(response2);
            })
        })
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [rerender]);

    const {desktop} = useScreenSize();

    const columns: Column[] = [
        { id: 'title', label: 'Title', minWidth: 50, align: 'center' },
        { id: 'publication-date', label: 'Publication date', minWidth: 50, align: 'center'},
        { id: 'sale-date', label: 'Sale date', minWidth: 50, align: 'center'},
        { id: 'closing-date', label: 'Closing date', minWidth: 50, align: 'center'},
        { id: 'status', label: 'Status', minWidth: 40, align: 'center'},
        { id: 'price', label: 'Price', minWidth: 40, align: 'center'},
    ];

    if (props.myProfile) {
        columns.push({ id: 'action', label: 'Action', minWidth: 80, align: 'center'});
    }

    const dateCell = (date: {seconds: number, nanoseconds: number} | undefined): JSX.Element => {
            return date ?
                <TableCell size='medium' align='center'>
                    {toDate(date).toLocaleString('default', {
                        day: 'numeric',
                        month: 'short',
                        year: 'numeric',
                        hour: 'numeric',
                        minute: 'numeric'
                    })}
                </TableCell> :
            <TableCell/>;
    }

    const activate = async (ad: Ad, user: User): Promise<void> => {
        await db.collection('dogAds').doc(ad.docID).set(
            {
                ...ad,
                status: 'active',
                saleDate: null,
                closingDate: null,
            }
        );
        await db.collection('users').doc(user.name).set({
            ...user,
            activeAds: user.activeAds + 1,
        });
    }

    const sell = async (ad: Ad, user: User): Promise<void> => {
        await db.collection('dogAds').doc(ad.docID).set({
            ...ad,
            status: 'sold',
            saleDate: new Date(),
            closingDate: null,
        });
        await db.collection('users').doc(user.name).set({
            ...user,
            activeAds: user.activeAds - 1,
        });
    }

    const handleChangePage = (event: React.MouseEvent<HTMLButtonElement, MouseEvent> | null, newPage: number): void => {
        setPage(newPage);
    };

    return (
        <div className='profile-page'>
            {desktop && (
                <div className='profile-info'>
                    <div className='avatar'><img src={user.avatar} alt={'avatar'}/></div>
                    <div>
                        <div className='name'>{user.name}</div>
                        <div className='phone'>Phone number: {user.phone}</div>
                        <div className='email'>Email: {user.email}</div>
                        <div className='date'>
                            Date of registration: {toDate(user.date).toLocaleString('default', {
                            day: 'numeric',
                            month: 'long',
                            year: 'numeric'})}
                        </div>
                    </div>
                </div>
            )}
            {!desktop && (
                <div className='profile-info-mobile'>
                    <div className='avatar'><img src={user.avatar} alt={'avatar'}/></div>
                    <div className='name'>{user.name}</div>
                    <div className='phone'>Phone number: {user.phone}</div>
                    <div className='email'>Email: {user.email}</div>
                    <div className='date'>Date of registration: {toDate(user.date).toLocaleString('default', {
                        day: 'numeric',
                        month: 'long',
                        year: 'numeric'})}
                    </div>
                </div>
            )}
            <TableContainer>
                <Table stickyHeader aria-label='sticky table'>
                    <TableHead>
                        <TableRow>
                            {columns.map((column) => {
                                return (
                                    <TableCell
                                        key={column.id}
                                        align={column.align}
                                        style={{minWidth: column.minWidth}}
                                    >
                                        {column.label}
                                    </TableCell>
                                );
                            })}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {
                            adsData.slice(page * 5, page * 5 + 5).map((row) => {
                                return (
                                    <TableRow role='checkbox' tabIndex={-1} key={row.id}>
                                        <TableCell size='medium' align='center'>{row.title}</TableCell>
                                        {dateCell(row.date)}
                                        {// @ts-ignore
                                            dateCell(row.saleDate)}
                                        {// @ts-ignore
                                            dateCell(row.closingDate)}
                                        <TableCell size='medium' align='center'>{row.status}</TableCell>
                                        <TableCell size='medium' align='center'>{row.price}</TableCell>
                                        {props.myProfile ?
                                            <TableCell size='medium' align='center'>
                                                {row.status === 'closed' ?
                                                    <IconButton
                                                        aria-label='lose'
                                                        size='small'
                                                        color='primary'
                                                        onClick={() => {
                                                            // @ts-ignore
                                                            activate(row, user).then(() => setRerender(cur => {
                                                                setRerender(cur + 1)
                                                            }));
                                                        }}
                                                    >
                                                        <Unarchive fontSize='small'/>
                                                    </IconButton> :
                                                    row.status === 'active' ?
                                                        <>
                                                            <IconButton
                                                                aria-label='lose'
                                                                size='small'
                                                                color='primary'
                                                                onClick={() => {
                                                                    // @ts-ignore
                                                                    sell(row, user).then(() => setRerender(cur => {
                                                                        setRerender(cur + 1)
                                                                    }));
                                                                }}
                                                            >
                                                                <SellIcon fontSize='small'/>
                                                            </IconButton>
                                                            <IconButton
                                                                aria-label='lose'
                                                                size='small'
                                                                color='primary'
                                                                onClick={() => {
                                                                    // @ts-ignore
                                                                    closeAd(row, user).then(() => setRerender(cur => {
                                                                        setRerender(cur + 1)
                                                                    }));
                                                                }}
                                                            >
                                                                <Archive fontSize='small'/>
                                                            </IconButton>
                                                        </> : null
                                                }
                                            </TableCell> : null
                                        }
                                    </TableRow>
                                );
                            })}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[5]}
                component='div'
                count={adsData.length}
                page={page}
                rowsPerPage={5}
                onPageChange={handleChangePage}
                className='table-pagination'
            />
        </div>
    );
};

export default Profile;
