import { Fields } from '../interfaces/Fields';

export const validateAd = (file: File, fields: Fields) => {
    for (let key in fields) {
        // @ts-ignore
        if (fields[key] === '') {
            return false;
        }
    }
    return file?.name !== undefined;
}
